PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE analysis_runs (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    run_id TEXT UNIQUE NOT NULL,              -- UUID for reference
    ticker TEXT NOT NULL,
    company_name TEXT,
    analysis_type TEXT NOT NULL,               -- fundamental, excellent, objective, buffett, taleb, contrarian, multi
    filing_type TEXT NOT NULL DEFAULT '10-K',  -- 10-K, 10-Q, 8-K, etc.
    years_analyzed TEXT,                       -- JSON array: ["2023", "2024"]
    status TEXT NOT NULL DEFAULT 'pending',    -- pending, running, completed, failed
    started_at TIMESTAMP,
    completed_at TIMESTAMP,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    error_message TEXT,
    config_json TEXT                           -- JSON with full configuration
, progress_message TEXT, progress_percent INTEGER DEFAULT 0, current_step TEXT, total_steps INTEGER DEFAULT 0);
CREATE TABLE analysis_results (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    run_id TEXT NOT NULL,                      -- Foreign key to analysis_runs
    ticker TEXT NOT NULL,
    fiscal_year INTEGER NOT NULL,
    filing_type TEXT NOT NULL DEFAULT '10-K',
    result_type TEXT NOT NULL,                 -- TenKAnalysis, BuffettAnalysis, etc.
    result_json TEXT NOT NULL,                 -- Full Pydantic model as JSON
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (run_id) REFERENCES analysis_runs(run_id) ON DELETE CASCADE
);
CREATE TABLE custom_prompts (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT UNIQUE NOT NULL,
    description TEXT,
    prompt_template TEXT NOT NULL,
    analysis_type TEXT NOT NULL,               -- fundamental, buffett, taleb, contrarian
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    is_active BOOLEAN DEFAULT 1
);
CREATE TABLE user_settings (
    key TEXT PRIMARY KEY,
    value TEXT NOT NULL,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);
CREATE TABLE file_cache (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    ticker TEXT NOT NULL,
    fiscal_year INTEGER NOT NULL,
    filing_type TEXT NOT NULL DEFAULT '10-K',
    file_path TEXT NOT NULL,
    file_hash TEXT,                            -- SHA256 for integrity
    downloaded_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    UNIQUE(ticker, fiscal_year, filing_type)
);
CREATE TABLE filing_types_cache (
    ticker TEXT PRIMARY KEY,
    filing_types TEXT NOT NULL,  -- JSON array of filing types
    cached_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);
CREATE TABLE workflows (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL UNIQUE,
    description TEXT,
    workflow_json TEXT NOT NULL,  -- Full workflow definition (steps, configs, etc.)
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);
CREATE TABLE workflow_runs (
    id TEXT PRIMARY KEY,  -- UUID
    workflow_id INTEGER NOT NULL,
    status TEXT NOT NULL DEFAULT 'pending',  -- pending, running, completed, failed, paused
    current_step_index INTEGER DEFAULT 0,
    total_steps INTEGER NOT NULL,

    -- Progress tracking
    started_at TIMESTAMP,
    completed_at TIMESTAMP,
    last_successful_step TEXT,

    -- Error tracking
    errors_json TEXT,  -- JSON array of errors

    -- Metadata
    created_by TEXT,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,

    FOREIGN KEY (workflow_id) REFERENCES workflows(id) ON DELETE CASCADE
);
CREATE TABLE workflow_step_outputs (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    workflow_run_id TEXT NOT NULL,
    step_id TEXT NOT NULL,
    output_json TEXT NOT NULL,  -- Serialized DataContainer
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,

    FOREIGN KEY (workflow_run_id) REFERENCES workflow_runs(id) ON DELETE CASCADE,
    UNIQUE(workflow_run_id, step_id)
);
CREATE TABLE workflow_step_logs (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    workflow_run_id TEXT NOT NULL,
    step_id TEXT NOT NULL,
    log_level TEXT NOT NULL,  -- INFO, WARNING, ERROR
    message TEXT NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,

    FOREIGN KEY (workflow_run_id) REFERENCES workflow_runs(id) ON DELETE CASCADE
);
DELETE FROM sqlite_sequence;
CREATE INDEX idx_runs_ticker ON analysis_runs(ticker);
CREATE INDEX idx_runs_status ON analysis_runs(status);
CREATE INDEX idx_runs_created ON analysis_runs(created_at DESC);
CREATE INDEX idx_runs_type ON analysis_runs(analysis_type);
CREATE INDEX idx_results_run ON analysis_results(run_id);
CREATE INDEX idx_results_ticker_year ON analysis_results(ticker, fiscal_year);
CREATE INDEX idx_prompts_type ON custom_prompts(analysis_type);
CREATE INDEX idx_prompts_active ON custom_prompts(is_active);
CREATE INDEX idx_cache_ticker ON file_cache(ticker);
CREATE INDEX idx_cache_ticker_year ON file_cache(ticker, fiscal_year);
CREATE VIEW latest_analyses AS
SELECT
    ar.ticker,
    ar.company_name,
    ar.analysis_type,
    ar.status,
    ar.completed_at,
    ar.run_id,
    COUNT(ares.id) as num_years
FROM analysis_runs ar
LEFT JOIN analysis_results ares ON ar.run_id = ares.run_id
WHERE ar.status = 'completed'
GROUP BY ar.ticker, ar.analysis_type
HAVING ar.completed_at = (
    SELECT MAX(completed_at)
    FROM analysis_runs
    WHERE ticker = ar.ticker AND analysis_type = ar.analysis_type
);
CREATE VIEW analysis_stats AS
SELECT
    analysis_type,
    status,
    COUNT(*) as count,
    DATE(created_at) as date
FROM analysis_runs
GROUP BY analysis_type, status, DATE(created_at);
CREATE INDEX idx_filing_types_cache_ticker ON filing_types_cache(ticker);
CREATE INDEX idx_filing_types_cache_cached_at ON filing_types_cache(cached_at);
CREATE INDEX idx_workflows_name ON workflows(name);
CREATE INDEX idx_workflows_created ON workflows(created_at DESC);
CREATE INDEX idx_workflow_runs_workflow ON workflow_runs(workflow_id);
CREATE INDEX idx_workflow_runs_status ON workflow_runs(status);
CREATE INDEX idx_workflow_runs_created ON workflow_runs(created_at DESC);
CREATE INDEX idx_step_outputs_run ON workflow_step_outputs(workflow_run_id);
CREATE INDEX idx_step_outputs_step ON workflow_step_outputs(step_id);
CREATE INDEX idx_step_logs_run ON workflow_step_logs(workflow_run_id);
CREATE INDEX idx_step_logs_level ON workflow_step_logs(log_level);
CREATE VIEW workflow_run_details AS
SELECT
    wr.id as run_id,
    wr.workflow_id,
    w.name as workflow_name,
    w.description as workflow_description,
    wr.status,
    wr.current_step_index,
    wr.total_steps,
    wr.started_at,
    wr.completed_at,
    wr.last_successful_step,
    wr.errors_json,
    (wr.current_step_index * 100.0 / wr.total_steps) as progress_percent,
    COUNT(DISTINCT wsl.id) as log_count,
    COUNT(DISTINCT wso.id) as step_output_count
FROM workflow_runs wr
JOIN workflows w ON wr.workflow_id = w.id
LEFT JOIN workflow_step_logs wsl ON wr.id = wsl.workflow_run_id
LEFT JOIN workflow_step_outputs wso ON wr.id = wso.workflow_run_id
GROUP BY wr.id;
COMMIT;
